sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//bubble task comments, work notes, and state information up to a case record if it exists

	//find related m2m for this record and case
	var m2m = new GlideRecord('x_casus_csm_m2m_tasks_cases');
	m2m.addQuery('task', current.sys_id);
	m2m.query();

	if(m2m.next()) {
		//get the case record from the m2m and update either its comments or work notes
		var cs = new GlideRecord('x_casus_csm_case');

		if(cs.get(m2m.getValue('case'))) {

			var stringToPost = current.number + " updated:\n\n"; //concat all changes into one update, not to flood the activity, worknotes
			var updateM2m = false; //flag to trigger m2m update when an important field changes on task

			//determine which was updated, and note those changes, and update state in the m2m record
			if(current.state.changes()) {
				stringToPost += "State changed to " + current.state.getDisplayValue() + "\n\n";

				m2m.state = current.state.getDisplayValue();
				updateM2m = true;
			}

			if(current.short_description.changes()) {
				stringToPost += "Short Description changed to " + current.short_description + "\n\n";

				m2m.short_description = current.short_description;
				updateM2m = true;
			}

			if(current.work_notes.changes()) {
				stringToPost += current.work_notes.getJournalEntry(1);
			} 

			if(current.comments.changes()) {
				stringToPost += current.comments.getJournalEntry(1);
			}

			if(updateM2m) {
				m2m.update();
			}

			cs.work_notes = stringToPost;
			cs.update();
		}	
	}

})(current, previous);
