sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//create a new case as long as one has not already been created for the user.
	var cs = new GlideRecord('x_casus_csm_case');
	cs.addQuery('client', current.variables.requester);
	cs.addActiveQuery();
	cs.query();

	var csm2m = new GlideRecord('x_casus_csm_m2m_tasks_cases');

	//if a record exists, just add this item to its related list
	if(cs.next()) {
		csm2m.initialize();

		csm2m.setValue("case", cs.sys_id);
		csm2m.task = current.sys_id;
		csm2m.task_type = current.getTableName();
		csm2m.state = current.state.getDisplayValue();
		csm2m.short_description = current.short_description;
		csm2m.insert();

		current.variables.case_number = cs.sys_id;
		if(cs.assigned_to != '') {
			current.variables.case_worker = cs.assigned_to;
		}

		current.update();

	} else { //if not, create a new case for this user

		var cs2 = new GlideRecord('x_casus_csm_case');
		cs2.initialize();

		cs2.client = current.variables.requester;
		cs2.assignment_group = current.cat_item.group;
		cs2.insert();

		csm2m.initialize();
		csm2m.setValue("case", cs2.sys_id);
		csm2m.task = current.sys_id;
		csm2m.task_type = current.getTableName();
		csm2m.state = current.state.getDisplayValue();
		csm2m.short_description = current.short_description;
		csm2m.insert();

		current.variables.case_number = cs2.sys_id;
		current.update();
	}

})(current, previous);
