sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
	var link = current.getLink();
	var border = 'border: 1px solid #278efc;';
	var color = 'color: #ffffff;';
	var fontSize = 'font-size: 16px;';
	var fontFamily = 'font-family: Helvetica, Arial, sans-serif;';
	var textDecoration = 'text-decoration: none; border-radius: 7px; letter-spacing: 1px;';
	var webKitBorder = '-webkit-border-radius: 7px;';
	var mozBorder = '-moz-border-radius: 7px;';
	var display = 'display: block;';
	var padding = 'padding: 10px;';

	template.print('<a href="' + link + '"');
	template.print('style="' + border + color + fontSize + fontFamily + textDecoration + webKitBorder + mozBorder + display + padding);
	template.print('">');
	template.print(gs.getMessage('View this Case'));
	template.print('</a>');

})(current, template, email, email_action, event);
